export const checkUserLoggedIn = () => {    
    if(localStorage.getItem('auth_token') != ''){
        return true;
    } else {
        return false;
    }
};

export const isLoginRequired = (isBooleanReq) => {    
    if(isBooleanReq){
        if(!checkUserLoggedIn){
            window.location.href = '/login';
        } else {
            window.location.href = '/list';
        }
    }
};
