import React from 'react';
import ReactDOM from 'react-dom';
import {checkUserLoggedIn} from './Utils.js';
import {
	BrowserRouter as Router,
	Switch,
	Redirect,
	Route,
	NavLink,
	useRouteMatch,
	Link
  } from "react-router-dom";
import axios from "axios";

class List extends React.Component{

	state = {
		users: [],
		srctext: ''
	}

	getUsersList() {
		  const formData = new FormData();
		  formData.append("action", "userslist");
		  formData.append("first_name", "first_name");
		  axios({
			url: "http://192.168.100.7/api/register.php",
			method: "post",
			data: formData
		  }).then(
			response => {
				const users = response.data.data.user_detail;
				this.setState({ users });
			},
			error => {
			  console.log(error);
			}
		  );

	  }	

	  componentDidMount() {
		  this.getUsersList();
	  }
	  render() {
		if(!checkUserLoggedIn()){
			return <Redirect to='/login' />
		}		  
		return (
			<div>
				<input type="text" name="srckeyword" value="{this.srctext}" />
		  		<ul>
					{ this.state.users.map(user => <li><b>{user.id}.</b>{user.first_name}</li>)}
		  		</ul>
			</div>
		)
	  }

}

export default List;
