import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Redirect,
  Route,
  NavLink,
  useRouteMatch,
  Link
} from "react-router-dom";
import {checkUserLoggedIn} from './utils.js';
import logo from "./logo.svg";
import "./App.css";
import HeaderWithLogin from "./HeaderWithLogin";
import HeaderWithoutLogin from "./HeaderWithoutLogin";
import Tick from "./Tick";
import List from "./List";
import Signup from "./Signup";
import Login from "./Login";
import Nomatchurl from "./Nomatchurl";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      names: ["gaurav", "alkesh", "hardik", "mayur", "ketan"],
      posts: [
        { id: 1, title: "Hello World", content: "Welcome to learning React!" },
        {
          id: 2,
          title: "Installation",
          content: "You can install React from npm."
        }
      ]
    };
  }

  render() {
    let HeaderNavigations;
    if (checkUserLoggedIn()) {
      HeaderNavigations = <HeaderWithLogin />;
    } else {
      HeaderNavigations = <HeaderWithoutLogin />;
    }    
    return (
      <Router>
        <div>
          {HeaderNavigations}
          {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
          <Switch>
            <Route path="/list">
              <List listArray={this.state.posts} />
            </Route>
            <Route path="/signup">
              <Signup />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
            <Route exact path="/">
              <Tick name="Gaurav" />
            </Route>
            <Route component={Nomatchurl} />
          </Switch>
        </div>
      </Router>
    );
  }
}
