import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Redirect,
  Route,
  NavLink,
  useRouteMatch,
  Link
} from "react-router-dom";
import {checkUserLoggedIn,clearUserAuthToken} from './utils.js';
import logo from "./logo.svg";
import "./App.css";
import Tick from "./Tick";
import List from "./List";
import Signup from "./Signup";
import Login from "./Login";
import Nomatchurl from "./Nomatchurl";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      names: ["gaurav", "alkesh", "hardik", "mayur", "ketan"],
      posts: [
        { id: 1, title: "Hello World", content: "Welcome to learning React!" },
        {
          id: 2,
          title: "Installation",
          content: "You can install React from npm."
        }
      ]
    };
  }

  render() {
    return (
      <Router>
        <div>
          <div className="topnav">
            <NavLink to="/" activeClassName="">
              Home
            </NavLink>
            <NavLink to="/list" activeClassName="active">
              List
            </NavLink>
            <NavLink to="/signup" activeClassName="active">
              Sign Up
            </NavLink>
            <NavLink to="/login" activeClassName="active">
              Login
            </NavLink>
            <a href="javascript:void(0)" onClick={clearUserAuthToken()}>Logout</a>

          </div>
          {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
          <Switch>
            <Route path="/list">
              <List listArray={this.state.posts} />
            </Route>
            <Route path="/signup">
              <Signup />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
            <Route exact path="/">
              <Tick name="Gaurav" />
            </Route>
            <Route component={Nomatchurl} />
          </Switch>
        </div>
      </Router>
    );
  }
}
