export const checkUserLoggedIn = () => {    
    if(localStorage.getItem('auth_token') != ''){
        return true;
    } else {
        return false;
    }
};

export const isLoginRequired = (isAuthReq) => {    
    if(isAuthReq == 'y'){
        if(!checkUserLoggedIn){
            window.location.href = '/login';
        }
    }
};
